+---
+layout: default
+title: Page Not Found
+canonical_url: /404.html
+---
+{% include nav.html %}
+<main class="front-section">
+  <div class="container">
+    <div class="masthead">
+      <div class="lead">404</div>
+      <h1>Page Not Found</h1>
+      <p class="light">The page you're looking for doesn't exist. It might have been moved, deleted, or you entered the wrong URL.</p>
+      
+      <div class="flex">
+        <a href="/" class="btn">Go Home</a>
+        <a href="/doc/" class="btn">Documentation</a>
+      </div>
+    </div>
+  </div>
+</main>
+
+<style>
+.masthead .lead {
+  font-size: 8rem;
+  color: var(--accent-color);
+  margin-bottom: 1rem;
+}
+
+.masthead h1 {
+  font-size: 2.5rem;
+  margin-bottom: 1rem;
+}
+
+.masthead p {
+  font-size: 1.25rem;
+  margin-bottom: 3rem;
+}
+
+.masthead .flex {
+  justify-content: center;
+  flex-wrap: wrap;
+}
+
+@media (max-width: 525px) {
+  .masthead .lead {
+    font-size: 6rem;
+  }
+  
+  .masthead h1 {
+    font-size: 2rem;
+  }
+  
+  .masthead p {
+    font-size: 1.1rem;
+  }
+  
+  .masthead .flex {
+    flex-direction: column;
+    align-items: center;
+  }
+  
+  .masthead .btn {
+    width: 100%;
+    max-width: 280px;
+  }
+}
+</style>
+
+<script>
+// Track 404 errors 
+if (typeof gtag !== 'undefined') {
+  gtag('event', 'page_view', {
+    page_title: '404 - Page Not Found',
+    page_location: window.location.href
+  });
+}
+
+// Auto-redirect after 3s
+const currentPath = window.location.pathname.toLowerCase();
+const redirects = {
+  '/docs': '/doc/',
+  '/documentation': '/doc/',
+  '/github': 'https://github.com/neovim/neovim',
+  '/repo': 'https://github.com/neovim/neovim'
+};
+
+if (redirects[currentPath]) {
+  console.log(`Redirecting ${currentPath} to ${redirects[currentPath]} in 3 seconds...`);
+  setTimeout(() => {
+    window.location.href = redirects[currentPath];
+  }, 3000);
+}

