index 2e887fab10..961152b71d 100644
--- a/src/nvim/shada.c
+++ b/src/nvim/shada.c
@@ -2489,7 +2489,8 @@ static ShaDaWriteResult shada_write(FileDescriptor *const sd_writer,
         fname = fm.fname;
       } else {
         const buf_T *const buf = buflist_findnr(fm.fmark.fnum);
-        if (ignore_buf(buf, &removable_bufs)) {
+        if (buf == NULL || buf->b_ffname == NULL
+            || set_has(ptr_t, &removable_bufs, (ptr_t)buf)) {
           continue;
