diff --git a/src/nvim/bufwrite.c b/src/nvim/bufwrite.c
index 1200660722..2853f3910b 100644
--- a/src/nvim/bufwrite.c
+++ b/src/nvim/bufwrite.c
@@ -387,6 +387,7 @@ static int buf_write_do_autocmds(buf_T *buf, char **fnamep, char **sfnamep, char
 {
   linenr_T old_line_count = buf->b_ml.ml_line_count;
   int msg_save = msg_scroll;
+  const int save_called_emsg = called_emsg;

   aco_save_T aco;
   bool did_cmd = false;
@@ -463,9 +464,11 @@ static int buf_write_do_autocmds(buf_T *buf, char **fnamep, char **sfnamep, char
   if (!bufref_valid(&bufref)) {
     buf = NULL;
   }
+  // Check if autocmds caused an error (Vimscript exception or Lua error)
+  const bool autocmd_error = aborting() || (called_emsg > save_called_emsg);
   if (buf == NULL || (buf->b_ml.ml_mfp == NULL && !empty_memline)
       || did_cmd || nofile_err
-      || aborting()) {
+      || autocmd_error) {
     if (buf != NULL && (cmdmod.cmod_flags & CMOD_LOCKMARKS)) {
       // restore the original '[ and '] positions
       buf->b_op_start = orig_start;
@@ -478,7 +481,7 @@ static int buf_write_do_autocmds(buf_T *buf, char **fnamep, char **sfnamep, char
       semsg(_(e_no_matching_autocommands_for_buftype_str_buffer), curbuf->b_p_bt);
     }

-    if (nofile_err || aborting()) {
+    if (nofile_err || autocmd_error) {
       // An aborting error, interrupt or exception in the
       // autocommands.
       return FAIL;
