Vibe coded fix:

```diff
diff --git a/src/nvim/channel.c b/src/nvim/channel.c
index e417e14d5b..3a836d6951 100644
--- a/src/nvim/channel.c
+++ b/src/nvim/channel.c
@@ -38,6 +38,8 @@
 #include "nvim/memory_defs.h"
 #include "nvim/message.h"
 #include "nvim/msgpack_rpc/channel.h"
+// Max bytes of PTY output to buffer before terminal attaches.
+#define TERM_PENDING_LIMIT (1024 * 1024)
 #include "nvim/msgpack_rpc/server.h"
 #include "nvim/os/fs.h"
 #include "nvim/os/os_defs.h"
@@ -220,6 +222,8 @@ Channel *channel_alloc(ChannelStreamType type)
   chan->exit_status = -1;
   chan->streamtype = type;
   chan->detach = false;
+  chan->term_pending_buf = (garray_T)GA_EMPTY_INIT_VALUE;
+  chan->term_pending = false;
   assert(chan->id <= VARNUMBER_MAX);
   pmap_put(uint64_t)(&channels, chan->id, chan);
   return chan;
@@ -297,6 +301,7 @@ static void channel_destroy(Channel *chan)
   callback_reader_free(&chan->on_data);
   callback_reader_free(&chan->on_stderr);
   callback_free(&chan->on_exit);
+  ga_clear(&chan->term_pending_buf);

   multiqueue_free(chan->events);
   xfree(chan);
@@ -659,7 +664,7 @@ size_t on_job_stderr(RStream *stream, const char *buf, size_t count, void *data,
 static size_t on_channel_output(RStream *stream, Channel *chan, const char *buf, size_t count,
                                 bool eof, CallbackReader *reader)
 {
-  if (chan->term) {
+  if (chan->term || chan->term_pending) {
     if (count) {
       const char *p = buf;
       const char *end = buf + count;
@@ -674,8 +679,20 @@ static size_t on_channel_output(RStream *stream, Channel *chan, const char *buf,
         p += clen;
       }
     }
+  }

+  if (chan->term) {
     terminal_receive(chan->term, buf, count);
+  } else if (chan->term_pending && count) {
+    if (chan->term_pending_buf.ga_itemsize == 0) {
+      ga_init(&chan->term_pending_buf, 1, 1024);
+    }
+    size_t pending_len = (size_t)chan->term_pending_buf.ga_len;
+    if (pending_len < TERM_PENDING_LIMIT) {
+      size_t avail = TERM_PENDING_LIMIT - pending_len;
+      size_t to_copy = count < avail ? count : avail;
+      ga_concat_len(&chan->term_pending_buf, buf, to_copy);
+    }
   }

   if (eof) {
@@ -831,6 +848,17 @@ void channel_terminal_open(buf_T *buf, Channel *chan)
   buf->b_p_channel = (OptInt)chan->id;  // 'channel' option
   channel_incref(chan);
   terminal_open(&chan->term, buf, topts);
+  if (chan->term_pending) {
+    if (chan->term && chan->term_pending_buf.ga_len > 0) {
+      terminal_receive(chan->term, chan->term_pending_buf.ga_data,
+                       (size_t)chan->term_pending_buf.ga_len);
+    }
+    ga_clear(&chan->term_pending_buf);
+    chan->term_pending = false;
+  }
+  if (chan->term && chan->exit_status >= 0) {
+    terminal_close(&chan->term, chan->exit_status);
+  }
 }

 static void term_write(const char *buf, size_t size, void *data)
diff --git a/src/nvim/channel.h b/src/nvim/channel.h
index 669b50cf86..78dafe0cfb 100644
--- a/src/nvim/channel.h
+++ b/src/nvim/channel.h
@@ -36,6 +36,8 @@ struct Channel {
                 ///< not FORCE self-exit.
   RpcState rpc;
   Terminal *term;
+  garray_T term_pending_buf;
+  bool term_pending;

   CallbackReader on_data;
   CallbackReader on_stderr;
diff --git a/src/nvim/eval/funcs.c b/src/nvim/eval/funcs.c
index 871794c4a1..2f914c1396 100644
--- a/src/nvim/eval/funcs.c
+++ b/src/nvim/eval/funcs.c
@@ -3599,6 +3599,7 @@ void f_jobstart(typval_T *argvars, typval_T *rettv, EvalFuncData fptr)
   } else if (!term) {
     channel_create_event(chan, NULL);
   } else {
+    chan->term_pending = true;
     if (rettv->vval.v_number <= 0) {
       return;
     }
```


