index 44fcc29ad..e92aa5376 100644
--- a/src/nvim/runtime.c
+++ b/src/nvim/runtime.c
@@ -2871,3 +2871,22 @@ bool script_autoload(const char *const name, const size_t name_len, const bool
 r
   xfree(tofree);
   return ret;
 }
+
+void validate_vimruntime_env(void)
+{
+  const char *vimruntime_env = vim_getenv("VIMRUNTIME");
+  if (vimruntime_env == NULL) {
+    emsg(_("E5009: $VIMRUNTIME is empty or unset"));
+    return;
+  }
+
+  if (!os_isdir(vimruntime_env)) {
+    semsg(_("E5009: Invalid $VIMRUNTIME: %s"), vimruntime_env);
+    return;
+  }
+
+  if (!os_file_is_readable(vimruntime_env)) {
+    semsg(_("E5009: Invalid $VIMRUNTIME: %s is not accessible or has insufficient permissions"),
+          vimruntime_env);
+  }
+}
