When looked if this PR can be related to #30905 I see (as a side effect) that multiline snippets from textEdit are not expanded here. Tried on
<details>

<summary>init.lua</summary>

```lua
local function create_server(opts)
  opts = opts or {}
  local server = {}
  server.messages = {}

  function server.cmd(dispatchers)
    local closing = false
    local handlers = opts.handlers or {}
    local srv = {}

    function srv.request(method, params, callback)
      table.insert(server.messages, {
        method = method,
        params = params,
      })
      local handler = handlers[method]
      if handler then
        handler(method, params, callback)
      elseif method == 'initialize' then
        callback(nil, {
          capabilities = opts.capabilities or {},
        })
      elseif method == 'shutdown' then
        callback(nil, nil)
      end
      local request_id = #server.messages
      return true, request_id
    end

    function srv.notify(method, params)
      table.insert(server.messages, {
        method = method,
        params = params,
      })
      if method == 'exit' then
        dispatchers.on_exit(0, 15)
      end
    end

    function srv.is_closing()
      return closing
    end

    function srv.terminate()
      closing = true
    end

    return srv
  end

  return server
end

local fun = table.concat({
  '${1|public,private,protected|} static function ${2:name}($3): ${4:void}',
  '{$0',
  '}',
}, '\n')

local textEdit = {
  range = {
    start = {line = 0, character = 0},
    ['end'] = {line = 0, character = 2},
  },
  newText = fun,
}

local completion_result = {
  isIncomplete = false,
  items = {
    {
      label = '/**',
      insertText = '/** ${1:hello} */',
      insertTextFormat = 2,
      kind = 15,
      detail = 'Snippet with non-word label',
    },
    {
      label = 'f',
      insertText = fun,
      insertTextFormat = 2,
      kind = 15,
      detail = 'Multiline snippet',
    },
    {
      label = 'fu',
      kind = vim.lsp.protocol.CompletionItemKind.Snippet,
      insertTextFormat = vim.lsp.protocol.InsertTextFormat.Snippet,
      detail = 'Multiline with textEdit',
      insertText = insertText,
      textEdit = textEdit,
    }
  },
}

local server = create_server({
  capabilities = {
    completionProvider = {},
  },
  handlers = {
    ['textDocument/completion'] = function (_, _, callback)
      callback(nil, completion_result)
    end,
  },
})

-- vim.o.completeopt = 'menuone,noinsert'
vim.o.completeopt = 'menuone,noselect'
vim.fn.complete = vim.schedule_wrap(vim.fn.complete)

vim.api.nvim_create_autocmd('UIEnter', {
  callback = function ()
    vim.lsp.start({
      name = 'dummy',
      cmd = server.cmd,
      on_attach = function (client, bufnr0)
        vim.lsp.completion.enable(true, client.id, bufnr0, {})
      end,
    })
  end
})
```

